import React, { useState } from 'react'
import DogCard from './DogCard'
import { Pagination } from 'antd'

const Dogs = ({ dogs }) => {
  const [currentPage, setCurrentPage] = useState(1)
  const [dogsPerPage] = useState(12)
  const [currentDogs, setCurrentDogs] = useState([])

  useEffect(() => {
    const lastDogIndex = currentPage * dogsPerPage
    const firstDogIndex = lastDogIndex - dogsPerPage
    setCurrentDogs(
      dogs.slice(firstDogIndex, lastDogIndex)
    )
  }, [])


  return (
    <div className='dog_cards__wrapper'>
      <div className="dog_cards">
        {
          currentDogs.map(dog => <DogCard key={dog.img} dog={dog} />)
        }
      </div>
      < Pagination
        hideOnSinglePage
        defaultPageSize={dogsPerPage}
        total={dogs.length}
        onChange={pageNumber => setCurrentPage(pageNumber)}
      />
    </div>
  )
}

export default Dogs
