import React, { useEffect } from 'react'
import { HeartOutlined } from '@ant-design/icons'

const DogCard = ({ dog, keepTheDog }) => {
  const { img, breed, subbreed, liked } = dog

  const handleAddDog = e => {
    e.preventDefault()
    const myDogs = JSON.parse(localStorage.getItem('dogs'))
    const index = myDogs.findIndex(dog => dog.img === img)
    console.log(index)
    // localStorage.setItem('dogs', JSON.stringify(myUpdatedDogs))
    // keepTheDog(img)
  }

  const likedOrNot = !!liked ? 'deeppink' : ''

  return (
    <div className="dog_card_wraper">
      <div className="dog_card">
        <div className="dog_card__left btn" onClick={handleAddDog} style={{ background: likedOrNot }}>
          <HeartOutlined /><div className='btn--add'>keep</div>
        </div>
        <div className="dog_card__right">
          <img src={img} alt={img} />
          <div className="dog_card__right__body">
            <div className="dog_card__right__body__text">{breed}</div>
            <div className="dog_card__right__body__text">{subbreed}</div>
          </div>
        </div>
      </div>
    </div>
  )
}

export default DogCard
