import React, { useEffect, useState } from 'react'
import axios from 'axios'
import DogCard from './DogCard'
import { Pagination } from 'antd'

const Dogs = ({ dogs }) => {
  const [currentPage, setCurrentPage] = useState(1)
  const [dogsPerPage, setDogsPerPage] = useState(12)

  const lastDogIndex = currentPage * dogsPerPage
  const firstDogIndex = lastDogIndex - dogsPerPage
  const currentDogs = dogs.slice(firstDogIndex, lastDogIndex)


  return (
    <div>
      <div className="dog_cards">
        {
          currentDogs.map(dog => <DogCard dog={dog} />)
        }
      </div>
      < Pagination />
    </div>
  )
}

export default Dogs
