import React, { useEffect, useState } from 'react'
import axios from 'axios'
import { Select, Button } from 'antd'
const { Option } = Select

const MenuHorizontal = ({ handleBreedSelection }) => {
  const MAX_SELECTED_BREEDS = 5
  const [breedSelection, setBreedSelection] = useState([])
  const [options, setOptions] = useState([])

  useEffect(() => {
    const getAllBreeds = async () => {
      const res = await axios.get(`https://dog.ceo/api/breeds/list/all`)
      const breeds = res.data.message
      setOptions(Object.keys(breeds))
    }

    getAllBreeds()
  }, [])

  const onChange = value => {
    setBreedSelection(value)
  }

  const isBreedLimitReached = () => {
    breedSelection.length >= MAX_SELECTED_BREEDS
  }

  return (
    <div className="menu_horizontal">
      <Select
        mode="multiple"
        allowClear
        placeholder="Find Your Doggo"
        style={{ width: '100%' }}
        onChange={onChange}
      >
        {
          options.map(option => {
            return <Option key={option} disabled={isBreedLimitReached} value={option}>{option}</Option>
          })
        }
      </Select>
      <Button onClick={() => handleBreedSelection(breedSelection)}>Search</Button>
    </div >
  )
}

export default MenuHorizontal
