import React, { useEffect, useState } from 'react'
import { Checkbox, Divider } from 'antd'
import Dogs from './Dogs'
import axios from 'axios'
import DogCard from './DogCard'
const CheckboxGroup = Checkbox.Group

const Content = ({ selectedBreeds }) => {
  const [temp, setTemp] = useState([])
  const [filteredBreeds, setFilteredBreeds] = useState([])
  const [dogs, setDogs] = useState([])
  const [checkedList, setCheckedList] = useState([]);
  const [indeterminate, setIndeterminate] = useState(true);
  const [checkAll, setCheckAll] = useState(false);

  const buildDogObjects = data => {
    return data.reduce((array, imgUrl) => {
      const [url, breed, subbreed] = imgUrl.match(/.*\/(\w+)\-?(\w*)\/.*$/)
      const obj = {}
      obj['breed'] = breed
      obj['subbreed'] = subbreed
      obj['img'] = url
      array.push(obj)
      return array
    }, [])
  }

  const getDogs = breeds => {
    let requests = breeds.map(b => axios.get(`https://dog.ceo/api/breed/${breed}/images`))
    return axios.all([
      ...requests
    ])
      .then(res => res.data.message)
      .catch(err => console.error(err))
  }

  useEffect(() => {
    const updateState = () => {
      selectedBreeds.map(breed => {
        getDogs(breed).then(res => {
          const dogObjects = buildDogObjects(res)
          setDogs([...dogs, ...dogObjects])
        })
      })
    }

    setDogs([])
    console.log(dogs.length)
    updateState()
  }, [selectedBreeds])

  // console.log(dogs)
  const onChange = list => {
    setCheckedList(list);
    setIndeterminate(!!list.length && list.length < selectedBreeds.length);
    setCheckAll(list.length === selectedBreeds.length);
    setFilteredBreeds(list)
  };

  const onCheckAllChange = e => {
    setCheckedList(e.target.checked ? selectedBreeds : []);
    setIndeterminate(false);
    setCheckAll(e.target.checked);
  };

  return (
    <div className="content">
      <div className="filter">
        {
          selectedBreeds.length > 0 && <>
            <Checkbox indeterminate={indeterminate} onChange={onCheckAllChange} checked={checkAll}>All</Checkbox>
            <CheckboxGroup options={selectedBreeds} value={selectedBreeds} onChange={onChange} />
          </>
        }
      </div>
      {/* <Dogs breed={breed} /> */}
      <div className="dog_cards">
        {
          dogs.length > 0 && dogs.map(dog => {
            return <DogCard key={dog.img} img_url={dog.img} breed={dog.breed} subbreed={dog.subbreed} />
          })
        }
      </div>
    </div>
  )
}

export default Content
