import React, { useEffect, useState } from 'react'
import { Checkbox, Divider } from 'antd'
import Dogs from './Dogs'
import axios from 'axios'
import DogCard from './DogCard'
const CheckboxGroup = Checkbox.Group

const Content = ({ breeds }) => {
  const searchedBreeds = breeds
  const [filteredBreeds, setFilteredBreeds] = useState([])
  const [dogs, setDogs] = useState([])
  const [checkedList, setCheckedList] = useState([]);
  const [indeterminate, setIndeterminate] = useState(true);
  const [checkAll, setCheckAll] = useState(false);
  // console.log({ filteredBreeds })

  useEffect(() => {
    const getDogs = () => {
      filteredBreeds.forEach(async (breed) => {
        let res = await axios.get(`https://dog.ceo/api/breed/${breed}/list`)
        let subBreeds = res.data.message

        if (subBreeds.length === 0) {
          let resp = await axios.get(`https://dog.ceo/api/breed/${breed}/images`)
          const data = resp.data.message
          setDogs([...dogs, ...data])
        } else {
          subBreeds.forEach(async (subBreed) => {
            let resp = await axios.get(`https://dog.ceo/api/breed/${breed}/${subBreed}/images`)
            const data = resp.data.message
            setDogs([...dogs, ...data])
          })
        }
      })
    }

    setFilteredBreeds(breeds)
    setCheckedList(breed)
    getDogs()

    console.log(dogs)
  }, [filteredBreeds])

  const onChange = list => {
    setCheckedList(list);
    setIndeterminate(!!list.length && list.length < searchedBreeds.length);
    setCheckAll(list.length === searchedBreeds.length);
    setFilteredBreeds(list)
  };

  const onCheckAllChange = e => {
    setCheckedList(e.target.checked ? searchedBreeds : []);
    setIndeterminate(false);
    setCheckAll(e.target.checked);
  };

  return (
    <div className="content">
      <div className="filter">
        {
          searchedBreeds.length > 0 && <>
            <Checkbox indeterminate={indeterminate} onChange={onCheckAllChange} checked={checkAll}>All</Checkbox>
            <CheckboxGroup options={searchedBreeds} value={checkedList} onChange={onChange} />
          </>
        }
      </div>
      {/* <Dogs breed={breed} /> */}
      <div className="dog_cards">
        {
          dogs.length > 0 && dogs.map(dog => {
            return <DogCard key={dog} img_url={dog} />
          })
        }
      </div>
    </div>
  )
}

export default Content
