import React, { useEffect, useState } from 'react'
import { Tree } from 'antd'


const Filter = () => {
  const [treeData, setTreeData] = useState([])

  useEffect(() => {
    const buildTreeData = list => {
      return list.reduce((container, breed) => {
        container.push({ title: breed, key: breed, children: [] })
        return container
      }, [])
    }

    let parents = buildTreeData(selectedBreeds.sort())

    let children = parents.map(parent => {
      let children = dogs.filter(breed => !!breed.subbreed & breed.breed === parent.title)
      let childrenList = children.map(ch => ch.subbreed)
      return { ...parent, children: buildTreeData([...new Set(childrenList)]) }
    })

    const treeData = [{ title: 'All', key: 'all', children: children }]
    console.log(treeData)
    setTreeData([...treeData])
  }, [selectedBreeds])

  return (
    <div>
      {
        // selectedBreeds.length > 0 && <>
        <Tree
          checkable
          // defaultSelectedKeys={['0-0-0', '0-0-1']}
          // autoExpandParent
          // defaultExpandedKeys={[...selectedBreeds]}
          // defaultCheckedKeys={[...selectedBreeds]}
          // onSelect={onSelect}
          // onCheck={onCheck}
          treeData={treeData}
        />
        // </>
      }
    </div>
  )
}

export default Filter
